import React, { useRef, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause, faDownload, faPen } from '@fortawesome/free-solid-svg-icons';

function VideoPlayer() {
  const videoRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);

  const togglePlayPause = () => {
    if (isPlaying) {
      videoRef.current.pause();
    } else {
      videoRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  return (
    <div className="video-player">
      <video ref={videoRef} className="video-element" controls={false}>
        {/* Add video source here */}
      </video>
      <div className="controls">
        <button className="edit-btn">
          <FontAwesomeIcon icon={faPen} size="lg" />
        </button>
        <input type="range" className="timeline-slider" />
        <button onClick={togglePlayPause} className="play-pause-btn">
          <FontAwesomeIcon icon={isPlaying ? faPause : faPlay} size="lg" />
        </button>
      </div>
      <div className="save-download">
        <button className="save-btn">Save</button>
        <button className="download-btn">
          <FontAwesomeIcon icon={faDownload} size="lg" />
        </button>
      </div>
    </div>
  );
}

export default VideoPlayer;
