import React, { useRef, useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause, faDownload, faPen } from '@fortawesome/free-solid-svg-icons';

function VideoPlayer({ videoFile, onSave }) {
  const videoRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [videoUrl, setVideoUrl] = useState(null);

  useEffect(() => {
    if (videoFile) {
      const url = URL.createObjectURL(videoFile);
      setVideoUrl(url);

      return () => {
        URL.revokeObjectURL(url);
      };
    }
  }, [videoFile]);

  const togglePlayPause = () => {
    if (isPlaying) {
      videoRef.current.pause();
    } else {
      videoRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  const handleSave = () => {
    if (onSave) {
      onSave();
    }
    alert("Changes saved successfully!");
  };

  const handleDownload = () => {
    if (!videoFile) {
      alert("No video file available for download");
      return;
    }
    const link = document.createElement('a');
    link.href = videoUrl;
    link.download = videoFile.name;
    link.click();
  };

  return (
    <div className="video-player">
      {videoUrl ? (
        <video ref={videoRef} className="video-element" controls={false} src={videoUrl} />
      ) : (
        <p>No video selected</p>
      )}

      <div className="controls">
        <button className="edit-btn">
          <FontAwesomeIcon icon={faPen} size="lg" />
        </button>
        <input type="range" className="timeline-slider" />
        <button onClick={togglePlayPause} className="play-pause-btn">
          <FontAwesomeIcon icon={isPlaying ? faPause : faPlay} size="lg" />
        </button>
      </div>

      <div className="save-download">
        <button onClick={handleSave} className="save-btn">Save</button>
        <button onClick={handleDownload} className="download-btn">
          <FontAwesomeIcon icon={faDownload} size="lg" />
        </button>
      </div>
    </div>
  );
}

export default VideoPlayer;
