// UploadSection.js
import React from 'react';

function UploadSection({ onCsvUploadSuccess }) {
  const handleCsvUpload = async (event) => {
    const file = event.target.files[0];
    if (file && file.type === 'text/csv') {
      const formData = new FormData();
      formData.append('file', file);  // Corrected to 'file' to match Flask app endpoint

      try {
        const response = await fetch('http://localhost:5000/upload_csv', {
          method: 'POST',
          body: formData,
        });

        if (response.ok) {
          console.log("CSV file uploaded successfully");
          onCsvUploadSuccess(true);  // Notify VideoPlayer about successful CSV upload
        } else {
          console.error("Failed to upload CSV file");
          onCsvUploadSuccess(false);
        }
      } catch (error) {
        console.error("Error uploading CSV file:", error);
        onCsvUploadSuccess(false);
      }
    } else {
      alert("Please upload a valid CSV file.");
    }
  };

  return (
    <div className="upload-section">
      <label htmlFor="csv-upload" className="upload-btn">Upload CSV</label>
      <input
        id="csv-upload"
        type="file"
        accept=".csv"
        style={{ display: 'none' }}
        onChange={handleCsvUpload}
      />
    </div>
  );
}

export default UploadSection;
