import React, { useRef, useState } from 'react';

function VideoPlayer({ csvFile, onSaveCoordinates, onEditId }) {
  const [videoSrc, setVideoSrc] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [editMode, setEditMode] = useState(null); // 'redraw' or 'edit-id'
  const [boundingBox, setBoundingBox] = useState(null); // store bounding box coordinates
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [drawing, setDrawing] = useState(false);

  const handleVideoUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      const url = URL.createObjectURL(file);
      setVideoSrc(url);
      setProgress(0);
      setIsPlaying(false);
    } else {
      alert('Please upload a valid video file.');
    }
  };

  const togglePlayPause = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleTimeUpdate = () => {
    if (videoRef.current) {
      const currentProgress = (videoRef.current.currentTime / videoRef.current.duration) * 100;
      setProgress(currentProgress);
    }
  };

  const handleSliderChange = (event) => {
    const newProgress = event.target.value;
    setProgress(newProgress);
    if (videoRef.current) {
      videoRef.current.currentTime = (videoRef.current.duration * newProgress) / 100;
    }
  };

  const handleEditOptionChange = (event) => {
    if (!csvFile) {
      alert('Please upload a CSV file before editing.');
      return;
    }

    const selectedOption = event.target.value;
    if (selectedOption === 'redraw') {
      handleRedrawBoundingBox();
    } else if (selectedOption === 'edit-id') {
      handleEditId();
    }
  };

  const handleRedrawBoundingBox = () => {
    console.log('Redraw Bounding Box Mode Activated');
    setEditMode('redraw');
    if (videoRef.current) videoRef.current.pause();
    setIsPlaying(false);
  };

  const handleEditId = () => {
    console.log('Edit ID Mode Activated');
    setEditMode('edit-id');
    if (videoRef.current) videoRef.current.pause();
    setIsPlaying(false);
  };

  const startDrawing = (e) => {
    if (editMode === 'redraw') {
      setDrawing(true);
      const rect = canvasRef.current.getBoundingClientRect();
      setBoundingBox({ x1: e.clientX - rect.left, y1: e.clientY - rect.top, x2: 0, y2: 0 });
    }
  };

  const drawBoundingBox = (e) => {
    if (drawing) {
      const rect = canvasRef.current.getBoundingClientRect();
      const x2 = e.clientX - rect.left;
      const y2 = e.clientY - rect.top;
      setBoundingBox((prevBox) => ({ ...prevBox, x2, y2 }));

      // Draw the rectangle on the canvas
      const ctx = canvasRef.current.getContext('2d');
      ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
      ctx.strokeStyle = 'red';
      ctx.lineWidth = 2;
      ctx.strokeRect(
        boundingBox.x1,
        boundingBox.y1,
        x2 - boundingBox.x1,
        y2 - boundingBox.y1
      );
    }
  };

  const endDrawing = () => {
    setDrawing(false);
  };

  const handleSaveChanges = () => {
    if (editMode === 'redraw' && boundingBox) {
      onSaveCoordinates(boundingBox);
      setBoundingBox(null);
    } else if (editMode === 'edit-id') {
      onEditId();
    }
    setEditMode(null);
    if (videoRef.current) videoRef.current.play();
    setIsPlaying(true);
  };

  return (
    <div className="video-player">
      {!videoSrc && (
        <>
          <label htmlFor="video-upload" className="upload-btn">Upload Video</label>
          <input
            id="video-upload"
            type="file"
            accept="video/*"
            style={{ display: 'none' }}
            onChange={handleVideoUpload}
          />
        </>
      )}

      {videoSrc && (
        <div style={{ position: 'relative', width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <div style={{ position: 'relative', width: '100%' }}>
            <video
              ref={videoRef}
              className="video-element"
              src={videoSrc}
              onTimeUpdate={handleTimeUpdate}
              onEnded={() => setIsPlaying(false)}
              controls={false}
              style={{ filter: editMode ? 'brightness(0.8)' : 'none' }}
            />
            {editMode && (
              <canvas
                ref={canvasRef}
                className="drawing-canvas"
                width={videoRef.current ? videoRef.current.videoWidth : 640}
                height={videoRef.current ? videoRef.current.videoHeight : 360}
                style={{ position: 'absolute', top: 0, left: 0, zIndex: 2 }}
                onMouseDown={startDrawing}
                onMouseMove={drawBoundingBox}
                onMouseUp={endDrawing}
              />
            )}
          </div>

          <div className="controls">
            <button onClick={togglePlayPause} className="upload-btn">
              {isPlaying ? 'Pause' : 'Play'}
            </button>

            <input
              type="range"
              min="0"
              max="100"
              value={progress}
              onChange={handleSliderChange}
              className="timeline-slider"
            />
          </div>

          <div className="save-download">
            {/* Dropdown for edit options */}
            <select onChange={handleEditOptionChange} className="edit-dropdown">
              <option value="">Select Edit Option</option>
              <option value="redraw">Redraw Bounding Boxes</option>
              <option value="edit-id">Edit ID</option>
            </select>

            <button className="save-btn" onClick={handleSaveChanges}>Save</button>
            <button className="download-btn">Download</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default VideoPlayer;
