import React, { useRef, useState, useEffect } from 'react';
import './VideoPlayer.css';

function VideoPlayer({ csvUploaded }) {
  const [videoSrc, setVideoSrc] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [editMode, setEditMode] = useState(null);
  const [showIdPopup, setShowIdPopup] = useState(false);
  const [currentId, setCurrentId] = useState("");
  const [newId, setNewId] = useState("");
  const [currentFrame, setCurrentFrame] = useState(0);
  const videoRef = useRef(null);
  const animationRef = useRef(null);

  const frameRate = 30;

  const handleVideoUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      const url = URL.createObjectURL(file);
      setVideoSrc(url);
      setProgress(0);
      setIsPlaying(false);
    } else {
      alert('Please upload a valid video file.');
    }
  };

  const togglePlayPause = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
        cancelAnimationFrame(animationRef.current);
      } else {
        videoRef.current.play();
        requestFrameUpdate();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleSliderChange = (event) => {
    const newProgress = event.target.value;
    setProgress(newProgress);
    if (videoRef.current) {
      videoRef.current.currentTime = (videoRef.current.duration * newProgress) / 100;
    }
  };

  const requestFrameUpdate = () => {
    if (videoRef.current && !videoRef.current.paused) {
      const currentFrame = Math.floor(videoRef.current.currentTime * frameRate);
      setCurrentFrame(currentFrame);
      const currentProgress = (videoRef.current.currentTime / videoRef.current.duration) * 100;
      setProgress(currentProgress);
      animationRef.current = requestAnimationFrame(requestFrameUpdate);
    }
  };

  const handleEditOptionChange = (event) => {
    if (!csvUploaded) {
      alert('Please upload a CSV file before editing.');
      return;
    }

    const selectedOption = event.target.value;
    if (selectedOption === 'edit-id') {
      handleEditId();
    }
  };

  const handleEditId = () => {
    setEditMode('edit-id');
    setShowIdPopup(true);
    if (videoRef.current) videoRef.current.pause();
    cancelAnimationFrame(animationRef.current);
    setIsPlaying(false);
  };

  const handleSaveId = async () => {
    try {
      const response = await fetch('http://localhost:5000/update_id', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ current_id: currentId, new_id: newId, frame: currentFrame })
      });

      const result = await response.json();
      if (response.ok) {
        console.log("ID updated successfully", result);
      } else {
        console.error("Failed to update ID", result.error);
      }
    } catch (error) {
      console.error("Error updating ID:", error);
    }
    setShowIdPopup(false);
    setCurrentId("");
    setNewId("");
  };

  useEffect(() => {
    return () => cancelAnimationFrame(animationRef.current);
  }, []);

  return (
    <div className="video-player">
      {/* Video Upload Section */}
      {!videoSrc && (
        <>
          <label htmlFor="video-upload" className="upload-btn">Upload Video</label>
          <input
            id="video-upload"
            type="file"
            accept="video/*"
            style={{ display: 'none' }}
            onChange={handleVideoUpload}
          />
        </>
      )}

      {/* Video Controls */}
      {videoSrc && (
        <div style={{ position: 'relative', width: '100%' }}>
          <video ref={videoRef} src={videoSrc} controls={false} />
          <div>
            <button onClick={togglePlayPause}>{isPlaying ? 'Pause' : 'Play'}</button>
            <input type="range" min="0" max="100" value={progress} onChange={handleSliderChange} />
          </div>
          <select onChange={handleEditOptionChange}>
            <option value="">Select Edit Option</option>
            <option value="edit-id">Edit ID</option>
          </select>
        </div>
      )}

      {showIdPopup && (
        <div className="id-popup">
          <h3>Update ID</h3>
          <input type="text" placeholder="Current ID" value={currentId} onChange={(e) => setCurrentId(e.target.value)} />
          <input type="text" placeholder="New ID" value={newId} onChange={(e) => setNewId(e.target.value)} />
          <button onClick={handleSaveId}>Save ID</button>
        </div>
      )}
    </div>
  );
}

export default VideoPlayer;
