import React, { useRef, useState, useEffect } from 'react';
import './VideoPlayer.css'; // Import the CSS file

function VideoPlayer({ onSaveCoordinates, onEditId }) {
  const [videoSrc, setVideoSrc] = useState(null);
  const [csvUploaded, setCsvUploaded] = useState(false); // Track CSV upload state
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [editMode, setEditMode] = useState(null); // 'redraw' or 'edit-id'
  const [showIdPopup, setShowIdPopup] = useState(false);
  const [currentId, setCurrentId] = useState("");
  const [newId, setNewId] = useState("");
  const [currentFrame, setCurrentFrame] = useState(0);
  const videoRef = useRef(null);
  const animationRef = useRef(null); // Ref for requestAnimationFrame

  const frameRate = 30;

  const handleVideoUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      const url = URL.createObjectURL(file);
      setVideoSrc(url);
      setProgress(0);
      setIsPlaying(false);
    } else {
      alert('Please upload a valid video file.');
    }
  };

  const handleCsvUpload = async (event) => {
    const file = event.target.files[0];
    if (file && file.type === 'text/csv') {
      const formData = new FormData();
      formData.append('csvFile', file);

      try {
        const response = await fetch('http://localhost:5000/upload-csv', {
          method: 'POST',
          body: formData,
        });

        const result = await response.json();
        if (result.success) {
          setCsvUploaded(true);
          alert('CSV uploaded successfully');
        } else {
          alert('Failed to upload CSV');
        }
      } catch (error) {
        console.error('Error uploading CSV:', error);
        alert('Failed to upload CSV');
      }
    } else {
      alert('Please upload a valid CSV file.');
    }
  };

  const togglePlayPause = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
        cancelAnimationFrame(animationRef.current);
      } else {
        videoRef.current.play();
        requestFrameUpdate();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleSliderChange = (event) => {
    const newProgress = event.target.value;
    setProgress(newProgress);
    if (videoRef.current) {
      videoRef.current.currentTime = (videoRef.current.duration * newProgress) / 100;
    }
  };

  const requestFrameUpdate = () => {
    if (videoRef.current && !videoRef.current.paused) {
      const currentFrame = Math.floor(videoRef.current.currentTime * frameRate);
      setCurrentFrame(currentFrame);
      const currentProgress = (videoRef.current.currentTime / videoRef.current.duration) * 100;
      setProgress(currentProgress);
      animationRef.current = requestAnimationFrame(requestFrameUpdate);
    }
  };

  const handleEditOptionChange = (event) => {
    if (!csvUploaded) {
      alert('Please upload a CSV file before editing.');
      return;
    }

    const selectedOption = event.target.value;
    if (selectedOption === 'redraw') {
      handleRedrawBoundingBox();
    } else if (selectedOption === 'edit-id') {
      handleEditId();
    }
  };

  const handleRedrawBoundingBox = () => {
    setEditMode('redraw');
    if (videoRef.current) videoRef.current.pause();
    cancelAnimationFrame(animationRef.current);
    setIsPlaying(false);
  };

  const handleEditId = () => {
    setEditMode('edit-id');
    setShowIdPopup(true); // Show the popup window
    if (videoRef.current) videoRef.current.pause();
    cancelAnimationFrame(animationRef.current);
    setIsPlaying(false);
  };

  const handleSaveId = () => {
    onEditId(currentId, newId);
    setShowIdPopup(false); // Hide the popup window
    setEditMode(null);
    setCurrentId("");
    setNewId("");
    if (videoRef.current) {
      videoRef.current.play();
      requestFrameUpdate();
    }
    setIsPlaying(true);
  };

  useEffect(() => {
    return () => cancelAnimationFrame(animationRef.current);
  }, []);

  return (
    <div className="video-player">
      {!videoSrc && (
        <>
          <label htmlFor="video-upload" className="upload-btn">Upload Video</label>
          <input
            id="video-upload"
            type="file"
            accept="video/*"
            style={{ display: 'none' }}
            onChange={handleVideoUpload}
          />
        </>
      )}

      {!csvUploaded && (
        <>
          <label htmlFor="csv-upload" className="upload-btn">Upload CSV</label>
          <input
            id="csv-upload"
            type="file"
            accept=".csv"
            style={{ display: 'none' }}
            onChange={handleCsvUpload}
          />
        </>
      )}

      {videoSrc && (
        <div style={{ position: 'relative', width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <div style={{ position: 'relative', width: '100%' }}>
            <video
              ref={videoRef}
              className="video-element"
              src={videoSrc}
              onEnded={() => setIsPlaying(false)}
              controls={false}
              style={{ filter: editMode ? 'brightness(0.8)' : 'none' }}
            />
            {editMode && <div className="dimming-overlay"></div>}
          </div>

          <div className="controls">
            <button onClick={togglePlayPause} className="upload-btn">
              {isPlaying ? 'Pause' : 'Play'}
            </button>

            <input
              type="range"
              min="0"
              max="100"
              value={progress}
              onChange={handleSliderChange}
              className="timeline-slider"
            />
            <span className="frame-display">Frame: {currentFrame}</span>
          </div>

          <div className="save-download">
            <select onChange={handleEditOptionChange} className="edit-dropdown">
              <option value="">Select Edit Option</option>
              <option value="redraw">Redraw Bounding Boxes</option>
              <option value="edit-id">Edit ID</option>
            </select>

            <button className="save-btn" onClick={handleSaveId}>Save</button>
            <button className="download-btn">Download</button>
          </div>
        </div>
      )}

      {showIdPopup && (
        <div className="id-popup">
          <h3>Update ID</h3>
          <input
            type="text"
            placeholder="Current ID"
            value={currentId}
            onChange={(e) => setCurrentId(e.target.value)}
          />
          <input
            type="text"
            placeholder="New ID"
            value={newId}
            onChange={(e) => setNewId(e.target.value)}
          />
          <button onClick={handleSaveId}>Save ID</button>
        </div>
      )}
    </div>
  );
}

export default VideoPlayer;
