import React, { useRef, useState } from 'react';

function VideoPlayer({ csvFile, onSaveCoordinates }) {
  const [videoSrc, setVideoSrc] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [editMode, setEditMode] = useState(null); // 'redraw' or 'edit-id'
  const [showEditIdPopup, setShowEditIdPopup] = useState(false);
  const [currentId, setCurrentId] = useState('');
  const [newId, setNewId] = useState('');
  const [displayedId, setDisplayedId] = useState(null); // ID to display on video overlay
  const videoRef = useRef(null);

  const handleVideoUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      const url = URL.createObjectURL(file);
      setVideoSrc(url);
      setProgress(0);
      setIsPlaying(false);
    } else {
      alert('Please upload a valid video file.');
    }
  };

  const togglePlayPause = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleTimeUpdate = () => {
    if (videoRef.current) {
      const currentProgress = (videoRef.current.currentTime / videoRef.current.duration) * 100;
      setProgress(currentProgress);
    }
  };

  const handleSliderChange = (event) => {
    const newProgress = event.target.value;
    setProgress(newProgress);
    if (videoRef.current) {
      videoRef.current.currentTime = (videoRef.current.duration * newProgress) / 100;
    }
  };

  const handleEditOptionChange = (event) => {
    if (!csvFile) {
      alert('Please upload a CSV file before editing.');
      return;
    }

    const selectedOption = event.target.value;
    if (selectedOption === 'redraw') {
      handleRedrawBoundingBox();
    } else if (selectedOption === 'edit-id') {
      handleEditId();
    }
  };

  const handleRedrawBoundingBox = () => {
    setEditMode('redraw');
    if (videoRef.current) videoRef.current.pause();
    setIsPlaying(false);
  };

  const handleEditId = () => {
    setEditMode('edit-id');
    setShowEditIdPopup(true); // Show the popup for ID editing
    if (videoRef.current) videoRef.current.pause();
    setIsPlaying(false);
  };

  const handleSaveChanges = async () => {
    if (editMode === 'redraw') {
      onSaveCoordinates();
    } else if (editMode === 'edit-id') {
      try {
        await fetch('http://127.0.0.1:5000/api/update_id', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ currentId, newId })
        });
        setDisplayedId(newId); // Display the new ID on video overlay
      } catch (error) {
        console.error('Error updating ID:', error);
      }
    }

    setEditMode(null);
    setShowEditIdPopup(false);
    setCurrentId('');
    setNewId('');
    if (videoRef.current) videoRef.current.play();
    setIsPlaying(true);
  };

  return (
    <div className="video-player">
      {!videoSrc && (
        <>
          <label htmlFor="video-upload" className="upload-btn">Upload Video</label>
          <input
            id="video-upload"
            type="file"
            accept="video/*"
            style={{ display: 'none' }}
            onChange={handleVideoUpload}
          />
        </>
      )}

      {videoSrc && (
        <div style={{ position: 'relative', width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <div style={{ position: 'relative', width: '100%' }}>
            <video
              ref={videoRef}
              className="video-element"
              src={videoSrc}
              onTimeUpdate={handleTimeUpdate}
              onEnded={() => setIsPlaying(false)}
              controls={false}
              style={{ filter: editMode ? 'brightness(0.8)' : 'none' }}
            />
            {editMode && <div className="dimming-overlay"></div>}
            {displayedId && (
              <div className="id-overlay">
                ID: {displayedId}
              </div>
            )}
          </div>

          <div className="controls">
            <button onClick={togglePlayPause} className="upload-btn">
              {isPlaying ? 'Pause' : 'Play'}
            </button>

            <input
              type="range"
              min="0"
              max="100"
              value={progress}
              onChange={handleSliderChange}
              className="timeline-slider"
            />
          </div>

          <div className="save-download">
            <select onChange={handleEditOptionChange} className="edit-dropdown">
              <option value="">Select Edit Option</option>
              <option value="redraw">Redraw Bounding Boxes</option>
              <option value="edit-id">Edit ID</option>
            </select>

            <button className="save-btn" onClick={handleSaveChanges}>Save</button>
            <button className="download-btn">Download</button>
          </div>

          {/* Popup Modal for Edit ID */}
          {showEditIdPopup && (
            <div className="edit-id-popup">
              <h3>Update ID</h3>
              <label>
                Current ID:
                <input
                  type="text"
                  value={currentId}
                  onChange={(e) => setCurrentId(e.target.value)}
                />
              </label>
              <label>
                Updated ID:
                <input
                  type="text"
                  value={newId}
                  onChange={(e) => setNewId(e.target.value)}
                />
              </label>
              <button onClick={handleSaveChanges} className="save-btn">Save</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default VideoPlayer;
